generated.css:
body {
  margin: 0;
  background: white;
  color: #666;
  font-family: 'Trebuchet MS', 'kai';
  font-weight: lighter;
}
a {
  text-decoration: none;
  color: inherit;
  transition: color 0.3s;
}
a:hover {
  color: #cd3f45;
}
.Blog {
  margin: 0 auto;
  padding: 0 20px;
  max-width: 600px;
}
.Blog__header {
  margin-top: 50px;
  text-align: center;
}
.Blog__title {
  font-size: 40px;
}
.Blog__links {
  padding: 0;
  text-align: center;
  overflow: hidden;
}
.Blog__link {
  list-style: none;
  display: inline-block;
}
.Blog__link:not(:first-child) {
  margin-left: 8px;
  padding-left: 8px;
  border-left: 1px solid #eee;
}
.Blog__body {
  margin-top: 50px;
  font-family: 'Trebuchet MS', 'kai';
}
.Blog__footer {
  margin-bottom: 100px;
}
.Articles__items {
  padding: 0;
}
.Articles__item {
  position: relative;
  padding-left: 140px;
  line-height: 80px;
  list-style: none;
  border-top: 1px dashed #eee;
  overflow: hidden;
  text-overflow: ellipsis;
}
.Articles__item-date {
  position: absolute;
  left: 0;
  font-size: 14px;
  color: rgba(102, 102, 102, 0.6);
}
.Articles__item-title {
  margin: 0;
  font-size: 16px;
  font-weight: normal;
  word-break: keep-all;
  white-space: nowrap;
}
.Article__header {
  padding: 30px 0;
  margin-bottom: 30px;
  border-bottom: 1px dashed #eee;
  font-family: 'Trebuchet MS', 'kai';
}
.Article__title {
  margin: 0;
  font-size: 32px;
  font-weight: lighter;
}
.Article__details {
  margin-top: 20px;
  color: rgba(102, 102, 102, 0.6);
  font-size: 14px;
  overflow: hidden;
  font-family: 'Trebuchet MS', 'kai';
}
.Article__date {
  float: left;
}
.Article__tags {
  float: left;
  margin-left: 20px;
}
.Article__tag:not(:first-child) {
  margin-left: 8px;
  padding-left: 8px;
  border-left: 1px solid #eee;
}
.Article__comments {
  float: right;
}
.Article__footer {
  padding: 30px 0;
  margin-top: 30px;
  border-top: 1px dashed #eee;
}
.Article__reply {
  padding: 10px;
  text-align: center;
  border-radius: 4px;
  border: 1px solid rgba(102, 102, 102, 0.6);
}
.markdown-body {
  color: #666;
  font-family: 'Trebuchet MS', 'kai';
}
.markdown-body pre {
  font-family: 'Trebuchet MS', 'kai';
}
.markdown-body code {
  font-family: 'Trebuchet MS', 'kai';
}
@keyframes rotating {
  0% {
    border-radius: 2px;
    transform: rotate(0deg);
  }
  45% {
    border-radius: 15px;
    transform: rotate(-180deg);
  }
  55% {
    border-radius: 15px;
    transform: rotate(-180deg);
  }
  0% {
    border-radius: 2px;
    transform: rotate(-360deg);
  }
}
.Loader {
  margin: 100px 0;
  width: 100%;
  text-align: center;
}
.Loader::before {
  display: inline-block;
  width: 30px;
  height: 30px;
  box-sizing: border-box;
  content: '
      font-family: @font-family;
    ';
  border: 2px solid rgba(102, 102, 102, 0.6);
  animation: rotating 1.5s ease-in-out infinite;
}
.Comment {
  position: relative;
  padding-left: 58px;
}
.Comment:not(:first-child) {
  margin-top: 30px;
}
.Comment__header {
  font-size: 14px;
}
.Comment__avatar {
  position: absolute;
  top: 0;
  left: 0;
  width: 32px;
  height: 32px;
  border-radius: 4px;
  overflow: hidden;
}
.Comment__avatar img {
  width: 100%;
}
.Comment__user span {
  margin-left: 8px;
  padding: 1px 4px;
  font-size: 12px;
  color: white;
  background: #cd3f45;
  border-radius: 4px;
}
.Comment__date {
  font-size: 12px;
  color: rgba(102, 102, 102, 0.6);
}
.Pagination {
  padding: 0;
  text-align: center;
}
.Pagination__item {
  list-style: none;
  display: inline-block;
  width: 32px;
  height: 32px;
  line-height: 32px;
  border-radius: 4px;
}
.Pagination__item:not(:first-child) {
  margin-left: 8px;
}
.Pagination__item a {
  display: block;
  width: 100%;
  height: 100%;
}
.Pagination__item--active {
  border: 1px solid rgba(102, 102, 102, 0.6);
}
@media (max-width: 767px) {
  .Articles__item {
    padding-left: 120px;
    line-height: 60px;
  }
  .Articles__item-title {
    font-size: 14px;
  }
  .Articles__item-date {
    font-size: 12px;
  }
  .Article__title {
    font-size: 20px;
  }
  .Article__details {
    font-size: 12px;
  }
}
.stats-container {
  position: fixed;
  bottom: 0;
  left: 0;
  right: 0;
  text-align: center;
  padding: 20px 0;
}
.stats-container .visitsdiv {
  display: inline-block;
  margin: 0 auto;
}
.stats-container .visitsdiv #finicount_views {
  color: #cd3f45;
}
.stats-container .empty-lines {
  height: 60px;
}
